import React from 'react';
import { useBudget } from '../../context/BudgetContext';

const Header = () => {
  const { budget } = useBudget();

  return (
    <header className="bg-white border-b border-gray-200 h-16 flex items-center justify-between px-6 shadow-sm">
      <div className="flex items-center">
        <h1 className="text-xl font-bold text-gray-800">OrçaWorks</h1>
      </div>

      <div className="text-center flex-1">
        <h2 className="text-lg font-medium text-gray-700">
          Novo Orçamento - {budget.project.name}
        </h2>
      </div>

      <div className="flex items-center space-x-4">
        <button className="bg-indigo-500 hover:bg-indigo-600 text-white px-4 py-2 rounded-full text-sm font-medium">
          Salvar
        </button>
        <button className="bg-green-700 hover:bg-green-800 text-white px-4 py-2 rounded-full text-sm font-medium">
          Exportar
        </button>
      </div>
    </header>
  );
};

export default Header;
