import React, { useState } from 'react';
import { useBudget } from '../../context/BudgetContext';

const NewChapterModal = () => {
  const { setShowNewChapterModal, addChapter } = useBudget();

  const [formData, setFormData] = useState({
    chapterNumber: '',
    header: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.chapterNumber || !formData.header) {
      alert('Preencha todos os campos obrigatórios');
      return;
    }

    const chapterKey = `CAR ${formData.chapterNumber}`;
    const success = addChapter(chapterKey, formData.header);

    if (!success) {
      alert(`Capítulo ${chapterKey} já existe`);
      return;
    }

    setShowNewChapterModal(false);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content max-w-md">
        <div className="bg-gray-100 px-6 py-4 border-b border-gray-200 rounded-t-lg">
          <div className="flex justify-between items-center">
            <h2 className="text-lg font-semibold text-gray-800">
              Adicionar Novo Capítulo
            </h2>
            <button
              className="text-gray-500 hover:text-gray-700"
              onClick={() => setShowNewChapterModal(false)}
            >
              &times;
            </button>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="p-6">
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              NÚMERO DO CAPÍTULO
            </label>
            <input
              type="text"
              name="chapterNumber"
              value={formData.chapterNumber}
              onChange={handleChange}
              className="form-input"
              placeholder="Ex: 10"
              required
            />
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              TÍTULO DO CAPÍTULO
            </label>
            <input
              type="text"
              name="header"
              value={formData.header}
              onChange={handleChange}
              className="form-input"
              placeholder="Ex: ESTRUTURA METÁLICA"
              required
            />
          </div>

          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              MODELO BASE (OPCIONAL)
            </label>
            <select
              name="template"
              className="form-input"
              disabled
            >
              <option value="">Selecione um modelo...</option>
            </select>
            <p className="text-xs text-gray-500 mt-1">
              Funcionalidade disponível em versões futuras
            </p>
          </div>

          <div className="flex justify-between">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => setShowNewChapterModal(false)}
            >
              Cancelar
            </button>

            <button
              type="submit"
              className="btn btn-primary"
            >
              Criar Capítulo
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default NewChapterModal;
