import React, { useState } from 'react';
import { useBudget } from '../../context/BudgetContext';

const ProjectSidebar = ({ isOpen }) => {
  const { budget, setActiveChapter } = useBudget();
  const [projects, setProjects] = useState([
    { id: 'PROJ-001', name: 'Moradia S. Pedro Estoril', client: 'Cliente Exemplo', active: true },
    { id: 'PROJ-002', name: 'Edifício Comercial Lisboa', client: 'Empresa ABC', active: false },
    { id: 'PROJ-003', name: 'Remodelação Apartamento', client: 'João Silva', active: false },
  ]);

  const [expandedChapters, setExpandedChapters] = useState({});

  const toggleExpandChapter = (chapterKey) => {
    setExpandedChapters(prev => ({
      ...prev,
      [chapterKey]: !prev[chapterKey]
    }));
  };

  const handleChapterClick = (chapterKey) => {
    setActiveChapter(chapterKey);
  };

  return (
    <aside 
      className={`${isOpen ? 'w-64' : 'w-0'} bg-white dark:bg-gray-800 border-r border-gray-200 dark:border-gray-700 shadow-sm transition-all duration-300 ease-in-out overflow-hidden`}
    >
      <div className="p-4 border-b border-gray-200 dark:border-gray-700 flex items-center justify-between">
        <h2 className="font-semibold text-gray-700 dark:text-gray-300">Projetos</h2>
        <button className="text-indigo-600 dark:text-indigo-400 hover:text-indigo-800 dark:hover:text-indigo-300 text-sm font-medium">
          Novo
        </button>
      </div>

      <div className="overflow-y-auto h-full">
        <div className="p-4 space-y-4">
          {projects.map(project => (
            <div 
              key={project.id}
              className={`rounded-lg p-3 cursor-pointer transition-all duration-150 ${
                project.active 
                  ? 'bg-indigo-50 dark:bg-indigo-900/20 border-l-4 border-indigo-500 dark:border-indigo-400' 
                  : 'hover:bg-gray-50 dark:hover:bg-gray-700 border-l-4 border-transparent'
              }`}
            >
              <div className="font-medium text-gray-800 dark:text-gray-200">{project.name}</div>
              <div className="text-sm text-gray-500 dark:text-gray-400">{project.client}</div>
            </div>
          ))}
        </div>

        {isOpen && (
          <div className="px-4 py-3 border-t border-gray-200 dark:border-gray-700">
            <h3 className="font-medium text-gray-700 dark:text-gray-300 mb-3">Capítulos</h3>
            <div className="space-y-2">
              {Object.entries(budget.chapters).map(([chapterKey, chapter]) => (
                <div key={chapterKey} className="text-sm">
                  <div 
                    className="flex items-center justify-between py-2 px-3 rounded-md cursor-pointer hover:bg-gray-100 dark:hover:bg-gray-700"
                    onClick={() => toggleExpandChapter(chapterKey)}
                  >
                    <span className="truncate text-gray-700 dark:text-gray-300">{chapter.header}</span>
                    <svg 
                      xmlns="http://www.w3.org/2000/svg" 
                      className={`h-4 w-4 text-gray-500 dark:text-gray-400 transition-transform ${expandedChapters[chapterKey] ? 'transform rotate-180' : ''}`} 
                      fill="none" 
                      viewBox="0 0 24 24" 
                      stroke="currentColor"
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </div>

                  {expandedChapters[chapterKey] && chapter.items && (
                    <div className="pl-4 py-1 space-y-1 border-l border-gray-200 dark:border-gray-700 ml-3">
                      {chapter.items.map((item, index) => (
                        <div 
                          key={index}
                          className="py-1 px-2 rounded cursor-pointer text-gray-600 dark:text-gray-400 hover:bg-gray-50 dark:hover:bg-gray-700 hover:text-indigo-600 dark:hover:text-indigo-400 text-xs"
                          onClick={() => handleChapterClick(chapterKey)}
                        >
                          {item.MATERIAL}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </aside>
  );
};

export default ProjectSidebar;
