import React from 'react';
import { useBudget } from '../../context/BudgetContext';
import ChapterItem from './ChapterItem';
import Summary from './Summary';

const ChapterList = () => {
  const { budget, setShowNewChapterModal } = useBudget();

  return (
    <div>
      <div className="flex justify-between items-center bg-gray-100 px-4 py-3 rounded-lg mb-4">
        <h2 className="text-base font-semibold text-gray-800">Capítulos do Orçamento</h2>
        <button 
          className="bg-indigo-500 hover:bg-indigo-600 text-white px-3 py-1 rounded-full text-sm"
          onClick={() => setShowNewChapterModal(true)}
        >
          + Capítulo
        </button>
      </div>

      <div className="space-y-4">
        {Object.entries(budget.chapters).map(([key, chapter]) => (
          <ChapterItem key={key} chapterKey={key} chapter={chapter} />
        ))}
      </div>

      <Summary />
    </div>
  );
};

export default ChapterList;
