import React, { useState } from 'react';
import { useBudget } from '../../context/BudgetContext';
import ItemList from './ItemList';

const ChapterItem = ({ chapterKey, chapter }) => {
  const { 
    activeChapter, 
    setActiveChapter, 
    calculateChapterTotal,
    formatCurrency,
    setShowEditItemModal,
    setCurrentItem
  } = useBudget();

  const isActive = activeChapter === chapterKey;
  const total = calculateChapterTotal(chapterKey);

  const handleAddItem = () => {
    setCurrentItem({
      chapterKey,
      item: {
        MATERIAL: '',
        UNIDADE: '',
        QTD: 0,
        VALOR_UNITARIO: 0,
        VALOR: 0
      },
      isNew: true
    });
    setShowEditItemModal(true);
  };

  return (
    <div className="chapter-item">
      <div 
        className={`chapter-header ${isActive ? 'active' : ''}`}
        onClick={() => setActiveChapter(chapterKey)}
      >
        <h3 className="font-semibold">{chapterKey}: {chapter.header}</h3>
        <span className="font-medium">{formatCurrency(total)}</span>
      </div>

      {isActive && (
        <div className="bg-white p-3">
          <ItemList 
            chapterKey={chapterKey} 
            items={chapter.items} 
          />

          <div 
            className="mt-2 p-2 border border-dashed border-gray-300 rounded text-center text-indigo-500 cursor-pointer hover:bg-gray-50"
            onClick={handleAddItem}
          >
            + Adicionar item
          </div>
        </div>
      )}
    </div>
  );
};

export default ChapterItem;
