import React from 'react';
import Header from './components/layout/Header';
import Sidebar from './components/layout/Sidebar';
import Tabs from './components/layout/Tabs';
import ChapterList from './components/budget/ChapterList';
import InternalControl from './components/budget/InternalControl';
import EditItemModal from './components/modals/EditItemModal';
import NewChapterModal from './components/modals/NewChapterModal';
import { useBudget } from './context/BudgetContext';

function App() {
  const { 
    showEditItemModal, 
    showNewChapterModal 
  } = useBudget();

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      <Header />

      <div className="flex flex-1 overflow-hidden">
        <Sidebar />

        <div className="flex-1 flex flex-col">
          <Tabs />

          <div className="flex-1 flex overflow-auto">
            <div className="w-3/5 overflow-auto p-4">
              <ChapterList />
            </div>

            <div className="w-2/5 overflow-auto p-4 border-l border-gray-200">
              <InternalControl />
            </div>
          </div>
        </div>
      </div>

      {showEditItemModal && <EditItemModal />}
      {showNewChapterModal && <NewChapterModal />}
    </div>
  );
}

export default App;
